options.cartocss:
@water: #dde9e9;
@waterlines: #aacccc;
@land: #f9f9f9;

Map {
  buffer-size: 128;
  background-color: @water;
}

#unified_basemap_layers[layer='ne_10m_coastline_2163']{
  line-color: @waterlines;
  line-width: 0.75;
  line-opacity: 1;
  line-join: round;
  line-cap: round;
}

#unified_basemap_layers[layer='ne_10m_lakes_2163'] {
  line-color: @waterlines;
  line-width: 2.5;
  line-opacity: 1;
  line-join: round;
  line-cap: round;

  /* Soften lines at lower zooms */
  [zoom<=7] {
    line-width: 2.5;
    line-color: lighten(desaturate(#aacccc,2%),2%);
  }
  [zoom<=5] {
    line-width: 1.5;
    line-color: lighten(desaturate(#aacccc,5%),5%);
  }

  /* Separate attachment because seams */
  ::fill {
    polygon-fill: @water;
    polygon-opacity: 1;
  }

  /* Remove small lakes at lower zooms */
  [scalerank>3][zoom<=5] {
    ::fill {
      polygon-opacity: 0;
    }
    line-opacity: 0;
  }
  [scalerank>6][zoom<=7] {
    ::fill {
      polygon-opacity: 0;
    }
    line-opacity: 0;
  }
}

#unified_basemap_layers[layer='ne_10m_rivers_lake_centerlines_2163'] {
  line-color: @waterlines;
  line-width: 1.5;
  line-opacity: 1;
  line-join: round;
  line-cap: round;

  [name='Mississippi'],
  [name='St. Lawrence'],
  [name='Columbia'],
  [name='Ohio'],
  [name='Hudson'],
  [name='Missouri'],
  [name='Rio Grande'] {
    line-width: 4;
  }
  [zoom<=8][name='Mississippi'],
  [zoom<=8][name='St. Lawrence'],
  [zoom<=8][name='Columbia'],
  [zoom<=8][name='Ohio'],
  [zoom<=8][name='Hudson'],
  [zoom<=8][name='Missouri'],
  [zoom<=8][name='Rio Grande'] {
    line-width: 2;
  }
  [zoom<=8][name!='Mississippi'][name!='St. Lawrence'][name!='Rio Grande'][name!='Ohio'][name!='Hudson'][name!='Columbia'][name!='Missouri'],
  [zoom<=6][name='Mississippi'],
  [zoom<=6][name='Columbia'],
  [zoom<=6][name='Ohio'],
  [zoom<=6][name='Hudson'],
  [zoom<=6][name='Missouri'],
  [zoom<=6][name='Rio Grande'] {
    line-width: 1;
    line-color: lighten(desaturate(@waterlines,2%),2%);

  }
  [zoom>=7][name!='Mississippi'][name!='St. Lawrence'][name!='Rio Grande'][name!='Ohio'][name!='Hudson'][name!='Columbia'][name!='Missouri']::labels,
  [zoom>=5][name='Mississippi']::labels,
  [zoom>=5][name='Columbia']::labels,
  [zoom>=5][name='Ohio']::labels,
  [zoom>=5][name='Hudson']::labels,
  [zoom>=5][name='Missouri']::labels,
  [zoom>=5][name='Rio Grande']::labels {
    text-name: [name];
    text-face-name: 'DejaVu Sans Oblique';
    text-fill: @waterlines;
    text-placement: line;
    text-halo-fill: @land;
    text-halo-radius: 1.5;
    text-size: 10;
    text-dy: -8;
    text-character-spacing: 2;
    text-spacing: 100;
    text-min-distance: 100;
  }

  [zoom<=6][name!='Mississippi'][name!='St. Lawrence'][name!='Rio Grande'][name!='Ohio'][name!='Hudson'][name!='Columbia'][name!='Missouri'] {
    line-width: 0.5;
    line-color: lighten(desaturate(@waterlines,5%),5%);
  }
  [zoom<=5][name!='Mississippi'][name!='St. Lawrence'][name!='Rio Grande'][name!='Ohio'][name!='Hudson'][name!='Columbia'][name!='Missouri'] {
    line-width: 0;
  }
  [zoom<=5][name='Mississippi'],
  [zoom<=5][name='St. Lawrence'],
  [zoom<=5][name='Columbia'],
  [zoom<=5][name='Ohio'],
  [zoom<=5][name='Hudson'],
  [zoom<=5][name='Missouri'],
  [zoom<=5][name='Rio Grande'] {
    line-width: 0.5;
    line-color: lighten(desaturate(@waterlines,2%),2%);
  }
}

#unified_basemap_layers[layer='ne_10m_admin_0_countries_lakes_2163'] {

  line-color: @land;
  line-width: 1;
  line-opacity: 1;
  line-join: round;
  line-cap: round;
  polygon-fill: @land;
  polygon-opacity: 1;

}

#canals{
  [zoom<=7] {
    line-simplify: 5;
  }
  line-color: #AF83D9;
  line-width: 1;
  line-cap: round;
  line-join: round;
  /*
  [opened > 1840] {
    line-color: #f09;
  }
  */

/*
  [name = 'Erie Canal']::highlight {
    line-color: yellow;
    line-width: 10;
    opacity: 0.2;
    line-cap: round;
    line-join: round;
  }
*/

  [zoom<=7] { line-simplify: 5; }
  [zoom<=6] { line-simplify: 10;}
  [zoom<=5] { line-simplify: 15;}
}

@textcolor: #666;
@halocolor: #f9f9f9;

Map {
  buffer-size: 128;
}

#canals_cities_basemap[rank=1][zoom>=5],
#canals_cities_basemap[rank=2][zoom>=6],
#canals_cities_basemap[rank>=3][zoom>=8]{
  // Note: have to use markers not shields to change svg color
  ::halo {
    marker-placement: point;
    marker-fill-opacity: 1;
    marker-line-width: 0;
    marker-type: ellipse;
    marker-width: 9;
    marker-fill: @halocolor;
  }
  marker-fill-opacity: 0.9;
  marker-line-color: @halocolor;
  marker-line-width: 1.5;
  marker-line-opacity: 1;
  marker-placement: point;
  //marker-type: ellipse;
  marker-file: url('https://raw.githubusercontent.com/mapbox/maki/mb-pages/src/circle-12.svg');
  marker-width: 6;
  marker-fill: @textcolor;

  marker-allow-overlap: true;
}

@default_size: 9;
@x_distance_positive: 3;
@y_distance_positive: 3;
@x_distance_negative: -3;
@y_distance_negative: -3;

#canals_cities_basemap[rank=1][zoom>=5]::labels,
#canals_cities_basemap[rank=2][zoom>=6]::labels,
#canals_cities_basemap[rank>=3][zoom>=8]::labels, {

  text-name: [town];
  text-face-name: 'DejaVu Sans Book';
  text-size: @default_size;
  [zoom>=6][rank=1] {
    text-size: @default_size + 3;
  }
  text-label-position-tolerance: 0;
  text-fill: @textcolor;
  text-halo-fill: @halocolor;
  text-halo-radius: 1.5;
  // Default is upper right from dot
  text-dy: @y_distance_negative;
  text-dx: @x_distance_positive;

  // Labels to float left instead
  [state='Illinois'],
  [state='Indiana'],
  [state='Ohio'][town!='Cincinnati'],
  [town='Bellefonte'],
  [town='Pittsburgh'],
  [town='Rochester'],
  [town='Newark'],
  [town='Oswego'],
  [town='Buffalo'],
  [town='Corning'],
  [town='Bristol'],
  [town='Reading'],
  [town='Buchanan'] {
    text-dx: @x_distance_negative;
  }

  // Labels to float below dot

  [town='New Brunswick'],
  [town='La Salle'],
  [town='Lawrenceburg'],
  [town='Akron'],
  [town='Albany'],
  [town='Athens'],
  [town='Utica'],
  [town='Reading'],
  [town='Bordentown'],
  [town='Philadelphia'],
  [town='Lynchburg'],
  [town='Toledo'],
  [town='Pittsburgh'],
  [town='Cincinnati'] {
    text-dy: @y_distance_positive;
  }

  text-allow-overlap: true;
  text-placement: point;
  text-placement-type: dummy;

}
