options.cartocss:
Map {
  buffer-size: 128;
}

#unified_basemap_layers[layer='ne_10m_coastline_2163']{
  line-color: #aacccc;
  line-width: 0.75;
  line-opacity: 1;
  line-join: round;
  line-cap: round;
}

#unified_basemap_layers[layer='ne_10m_lakes_2163'] {

  line-color: #aacccc;
  line-width: 2.5;
  line-opacity: 1;
  line-join: round;
  line-cap: round;

  /* Soften lines at lower zooms */
  [zoom<=7] {
    line-width: 2.5;
    line-color: lighten(desaturate(#aacccc,2%),2%);
  }
  [zoom<=5] {
    line-width: 1.5;
    line-color: lighten(desaturate(#aacccc,5%),5%);
  }

  /* Separate attachment because seams */
  ::fill {
    polygon-fill: #ddeeee;
    polygon-opacity: 1;
  }

  /* Remove small lakes at lower zooms */
  [scalerank>3][zoom<=5] {
    ::fill {
      polygon-opacity: 0;
    }
    line-opacity: 0;
  }
  [scalerank>6][zoom<=7] {
    ::fill {
      polygon-opacity: 0;
    }
    line-opacity: 0;
  }
}


#unified_basemap_layers[layer='ne_10m_rivers_lake_centerlines_2163'] {
  line-color: #aacccc;
  line-width: 1.5;
  line-opacity: 1;
  line-join: round;
  line-cap: round;

  [name='Mississippi'],
  [name='St. Lawrence'],
  [name='Columbia'],
  [name='Ohio'],
  [name='Hudson'],
  [name='Missouri'],
  [name='Rio Grande'] {
    line-width: 4;
  }
  [zoom<=8][name='Mississippi'],
  [zoom<=8][name='St. Lawrence'],
  [zoom<=8][name='Columbia'],
  [zoom<=8][name='Ohio'],
  [zoom<=8][name='Hudson'],
  [zoom<=8][name='Missouri'],
  [zoom<=8][name='Rio Grande'] {
    line-width: 2;
  }
  [zoom<=8][name!='Mississippi'][name!='St. Lawrence'][name!='Rio Grande'][name!='Ohio'][name!='Hudson'][name!='Columbia'][name!='Missouri'],
  [zoom<=6][name='Mississippi'],
  [zoom<=6][name='Columbia'],
  [zoom<=6][name='Ohio'],
  [zoom<=6][name='Hudson'],
  [zoom<=6][name='Missouri'],
  [zoom<=6][name='Rio Grande'] {
    line-width: 1;
    line-color: lighten(desaturate(#aacccc,2%),2%);
  }
  [zoom<=6][name!='Mississippi'][name!='St. Lawrence'][name!='Rio Grande'][name!='Ohio'][name!='Hudson'][name!='Columbia'][name!='Missouri'] {
    line-width: 0.5;
    line-color: lighten(desaturate(#aacccc,5%),5%);
  }
  [zoom<=5][name!='Mississippi'][name!='St. Lawrence'][name!='Rio Grande'][name!='Ohio'][name!='Hudson'][name!='Columbia'][name!='Missouri'] {
    line-width: 0;
  }
  [zoom<=5][name='Mississippi'],
  [zoom<=5][name='St. Lawrence'],
  [zoom<=5][name='Columbia'],
  [zoom<=5][name='Ohio'],
  [zoom<=5][name='Hudson'],
  [zoom<=5][name='Missouri'],
  [zoom<=5][name='Rio Grande'] {
    line-width: 0.5;
    line-color: lighten(desaturate(#aacccc,2%),2%);
  }
}

#unified_basemap_layers[layer='ne_10m_admin_0_countries_lakes_2163'] {

  line-color: white;
  line-width: 1;
  line-opacity: 1;
  line-join: round;
  line-cap: round;
  polygon-fill: white;
  polygon-opacity: 1;

}

Map {
  buffer-size: 128;
}

#canals_cities_basemap[rank=1],
#canals_cities_basemap[rank=2][zoom>=6],
#canals_cities_basemap[rank>=3][zoom>=8]{
  // Note: have to use markers not shields to change svg color
  ::halo {
    marker-placement: point;
    marker-fill-opacity: 1;
    marker-line-width: 0;
    marker-type: ellipse;
    marker-width: 12;
    marker-fill: white;
    [zoom<=5] {
      marker-fill: transparent;
      [zoom=5][town='New York'],
      [zoom=5][town='Philadelphia'],
      [zoom=5][town='Buffalo'] {
        marker-fill: white;
      }
    }
  }
  marker-fill-opacity: 0.9;
  marker-line-color: #FFF;
  marker-line-width: 1.5;
  marker-line-opacity: 1;
  marker-placement: point;
  //marker-type: ellipse;
  marker-file: url('https://raw.githubusercontent.com/mapbox/maki/mb-pages/src/circle-12.svg');
  marker-width: 10;
  marker-fill: #000000;

  marker-allow-overlap: true;
}

#canals_cities_basemap::labels {
[rank=1],
[rank=2][zoom>=6],
[rank>=3][zoom>=8] {
  text-name: [town];
  text-face-name: 'DejaVu Sans Book';
  text-size: 10;
  [zoom>=6] { 
    text-size: 14;
  }
  text-label-position-tolerance: 0;
  text-fill: #000;
  text-halo-fill: #FFF;
  text-halo-radius: 1;
  // Default is upper right from dot
  text-dy: -5;
  text-dx: 5;

  // Labels to float left instead
  [state='Illinois'],
  [state='Indiana'],
  [state='Ohio'][town!='Cincinnati'],
  [town='Bellefonte'],
  [town='Pittsburgh'], 
  [town='Rochester'],
  [town='Newark'],
  [town='Oswego'],
  [town='Buffalo'],
  [town='Corning'],
  [town='Bristol'],
  [town='Reading'],
  [town='Buchanan'] {
    text-dx: -5;
  }

  // Labels to float below dot

  [town='New Brunswick'],
  [town='La Salle'],
  [town='Lawrenceburg'],
  [town='Akron'],
  [town='Albany'],
  [town='Athens'],
  [town='Utica'],
  [town='Reading'],
  [town='Bordentown'],
  [town='Philadelphia'],
  [town='Lynchburg'],
  [town='Toledo'],
  [town='Pittsburgh'],
  [town='Cincinnati'] {
    text-dy: 5;
  }
  text-allow-overlap: true;
  text-placement: point;
  text-placement-type: dummy;
  [zoom<=4] {
    // Suppress everything at 4 and below 
    text-fill: transparent;
    text-halo-fill: transparent;
    // Except these, show them at 4
    [zoom=5][town='New York'],
    [zoom=5][town='Buffalo'],
    [zoom=5][town='Albany'] {
      text-halo-fill: #FFF;
      text-fill: #000;
    }
  }
  }
}
